import React, { useState, useEffect } from 'react';
import '../../src/BookSearch.css';
import { useNavigate } from 'react-router-dom'; 

const BookSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const navigate = useNavigate();
  const [notification, setNotification] = useState({ visible: false, message: '' });

  useEffect(() => {
    if (searchTerm) {
      const fetchData = async () => {
        try {
          const response = await fetch(`https://openlibrary.org/search.json?q=${searchTerm}&limit=${itemsPerPage}&page=${currentPage}`);
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          const data = await response.json();
          setSearchResults(data.docs || []);
          setError(null); // Reset error state if fetch is successful
        } catch (error) {
          setError(error.message);
          setSearchResults([]); // Clear previous results in case of error
        }
      };
      fetchData();
    } else {
      setSearchResults([]);
    }
  }, [searchTerm]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const addToBookshelf = (book) => {
    const currentBookshelf = localStorage.getItem('bookshelf') || '[]';
    const bookshelf = JSON.parse(currentBookshelf);
    bookshelf.push(book);
    localStorage.setItem('bookshelf', JSON.stringify(bookshelf));
    showNotification('Book added to your bookshelf!');
  };

  const handleNavigateToBookshelf = () => {
    navigate('/bookshelf'); // Navigate to the "/bookshelf" route
  };

  const showNotification = (message) => {
    setNotification({ visible: true, message });
    setTimeout(() => {
      setNotification({ visible: false, message: '' });
    }, 2000);
  };

  return (
    <div className="book-search">
      <h1>Book Search</h1>
      <input type="text" value={searchTerm} onChange={handleInputChange} placeholder="Search for books..." />
      <button onClick={handleNavigateToBookshelf}>Go to Bookshelf</button>
      <ul>
        {searchResults.map((book) => (
          <li key={book.key || book.isbn[0].isbn10}>
            <h2>{book.title}</h2>
            {book.author && <p>Author: {book.author.name[0]}</p>}
            {book.cover_i && (
              <img src={`https://covers.openlibrary.org/b/id/${book.cover_i}-S.jpg`} alt={book.title} />
            )}
            <button onClick={() => addToBookshelf(book)}>Add to Bookshelf</button>
          </li>
        ))}
      </ul>
      {notification.visible && (
        <div className="notification">
          <span className="tick-symbol">&#10003;</span> {notification.message}
        </div>
      )}
    </div>
  );
};

export default BookSearch;
